    public function random($sex){
    	$arr = $this->names();
    	$name = "";
    	if ($sex=="M") {
    		$i = random_int(0, sizeof($arr["males"]));
    		$name =$arr["males"][$i];	
    	}else{
    		$i = random_int(0, sizeof($arr["females"]));
    		$name =$arr["females"][$i];
    	}
		$i = random_int(0, sizeof($arr["males"]));
		$name = $name." ".$arr["males"][$i];
    	return $name;
    }
